import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {
  number = Math.floor(Math.random() * 10) + 1;
  slideOpts = {
    initialSlide: 0,
    speed: 500,
    pagination: false,
    autoHeight: true,
  };

  collection = [
    {
      author: "Miyamoto Musashi",
      desc: "Famous Samurai",
      img: "https://upload.wikimedia.org/wikipedia/commons/thumb/3/37/Miyamoto_Musashi-Portrait-Edo-period.png/1200px-Miyamoto_Musashi-Portrait-Edo-period.png",
      quote: "dolore quidem. Incidunt, adipisci. Atque, consequatur sequi exercitationem sunt, quos nobis voluptates debitis"
    },
    {
      author: "Cicero",
      desc: "Roman Statesman",
      img: "http://satyagraha.files.wordpress.com/2012/04/ciceropublicdomainbw1.jpg",
      quote: "tibus labore corporis! Ex dolores illum ipsam tempore quasi aliquam quis adipisciid sit earum similique voluptates ducimus re"
    },
    {
      author: "Caesar",
      desc: "Roman Usurper",
      img: "https://www.biography.com/.image/ar_1:1%2Cc_fill%2Ccs_srgb%2Cfl_progressive%2Cq_auto:good%2Cw_1200/MTY2NjgyMzA1MzM3NzYyODcx/gettyimages-51242589-2048x2048.jpg",
      quote: "Wedi Widi Wichi"
    },
    {
      author: "Marcus Aurelius",
      desc: "Roman Emperor",
      img: "https://www.onthisday.com/images/people/marcus-aurelius-medium.jpg",
      quote: "Wedi Widi Wichi"
    },
  ]

  constructor() { }

}
