import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: 'home.page.html',
  styleUrls: ['home.page.scss'],
})
export class HomePage {
  number = Math.floor(Math.random() * 10) + 1;
  slideOpts = {
    initialSlide: 1,
    speed: 500,
    pagination: false,
    autoHeight: true,
    // longSwipes: false
  };

  collection = [
    {
      author: "Miyamoto Musashi",
      desc: "Famous Samurai",
      img: "",
      quote: "dolore quidem. Incidunt, adipisci. Atque, consequatur sequi exercitationem sunt, quos nobis voluptates debitis"
    },
    {
      author: "Cicero",
      desc: "Roman Statesman",
      img: "",
      quote: "tibus labore corporis! Ex dolores illum ipsam tempore quasi aliquam quis adipisciid sit earum similique voluptates ducimus re"
    },
    {
      author: "Caesar",
      desc: "Roman Usurper",
      img: "",
      quote: "Wedi Widi Wichi"
    },
  ]

  constructor() { }

}
